--drop table message;

create table message (
	message_num int
	,message_string varchar
);

--delete from message;
insert into message (message_num, message_string) values (1, '');


select min(seq_end) as start_of_message from
(
	select
		current_message
		, seq_end 
		, cardinality (regexp_split_to_array(current_message, '')) as message_length
		, cardinality(array(select distinct unnest(regexp_split_to_array(current_message, '')))) as agg_message_length
		, case 
			when cardinality (regexp_split_to_array(current_message, '')) = cardinality(array(select distinct unnest(regexp_split_to_array(current_message, ''))))
			then 1
			else 0
		end as is_header_found
	from
	(
		select
			message_string
			, generate_series(1, length(message_string), 1) as seq_start
			, generate_series(4, length(message_string), 1) as seq_end
			, substring(message_string, generate_series(1, length(message_string), 1) , 4) as current_message
		from
			message
	) m
) c
where c.is_header_found = 1;